mapper.select:
SELECT  count(*) 
FROM INVITADOxSOCIO as I 
WHERE
 I.IdSocio = #{codigosocio} 
 AND MONTH(I.FechaIngreso) = MONTH(NOW());

mapper.update:
UPDATE

INVITADOxSOCIO

SET


INVITADOxSOCIO.Monto     =	#{monto}


WHERE INVITADOxSOCIO.IdInvitadoSocio =  #{codigo}

;

mapper.select:
SELECT MEMBRESIA.IdSocio AS codigo, PERSONA.Nombres AS nombre, PERSONA.ApePaterno AS apaterno, PERSONA.ApeMaterno AS amaterno
FROM MEMBRESIA, PERSONA
WHERE MEMBRESIA.IdSocio = PERSONA.IdPersona AND MEMBRESIA.Estado='Activo' AND PERSONA.Nombres LIKE #{nombre} ;

SELECT INVITADOxSOCIO.IdInvitadoSocio AS codigo, INVITADOxSOCIO.IdSocio AS codigosocio, INVITADO.IdInvitado AS codigoinvitado, PERSONA.Nombres AS nombre, PERSONA.ApePaterno AS apaterno, PERSONA.ApeMaterno AS amaterno, PERSONA.NumeroDocumento AS dni, INVITADOxSOCIO.FechaIngreso AS fechaingreso, INVITADOxSOCIO.Monto AS monto, INVITADOxSOCIO.Estado AS estado
FROM (INVITADO , PERSONA) LEFT JOIN INVITADOxSOCIO
ON INVITADOxSOCIO.IdInvitado = INVITADO.IdInvitado WHERE INVITADO.IdInvitado = PERSONA.IdPersona AND INVITADOxSOCIO.IdInvitadoSocio = #{codigo}

SELECT INVITADOxSOCIO.IdInvitadoSocio AS codigo, INVITADOxSOCIO.IdSocio AS codigosocio, INVITADO.IdInvitado AS codigoinvitado, PERSONA.Nombres AS nombre, PERSONA.ApePaterno AS apaterno, PERSONA.ApeMaterno AS amaterno, PERSONA.NumeroDocumento AS dni, INVITADOxSOCIO.FechaIngreso AS fechaingreso, INVITADOxSOCIO.Monto AS monto, INVITADOxSOCIO.Estado AS estado
FROM (INVITADO , PERSONA) LEFT JOIN INVITADOxSOCIO
ON INVITADOxSOCIO.IdInvitado = INVITADO.IdInvitado WHERE INVITADO.IdInvitado = PERSONA.IdPersona AND INVITADO.IdInvitado = #{codigo}

mapper.update:
UPDATE

INVITADOxSOCIO

SET

INVITADOxSOCIO.FechaIngreso    =	#{fechaingreso} ,

INVITADOxSOCIO.Estado     =	#{estado}


WHERE INVITADOxSOCIO.IdInvitadoSocio =  #{codigo}

;
