mapper.select:
select PLANTILLA_EVENTO.IdPlantillaEvento as codigo ,TIPO_EVENTO.nombre as tipo ,PLANTILLA_EVENTO.nombre as nombre ,PLANTILLA_EVENTO.FechaInicio as limInicio ,PLANTILLA_EVENTO.FechaFin as limFin from PLANTILLA_EVENTO left JOIN TIPO_EVENTO on TIPO_EVENTO.IdTipoEvento=PLANTILLA_EVENTO.IdTipoEvento
PLANTILLA_EVENTO.nombre like #{nombre}
AND TIPO_EVENTO.IdTipoEvento = #{tipo}
AND PLANTILLA_EVENTO.FechaInicio >= #{limInicio}
AND PLANTILLA_EVENTO.FechaFin <= #{limFin}
AND PLANTILLA_EVENTO.Estado='A'
order by 1 asc

mapper.update:
update PLANTILLA_EVENTO set nombre=#{nombre}
 ,IdTipoEvento=#{idtipo}
 ,FechaInicio=#{limiteInicio}
 ,FechaFin=#{limiteFin}
 where IdPlantillaEvento= #{codigo}
