mapper.select:
select PRODUCTO_PROV.IdProductoProv as idProdProveedor ,PRODUCTO_PROV.IdProveedor as proveedor ,PRODUCTO_PROV.Nombre as nombre ,PRODUCTO_PROV.Descripcion as descripcion ,PRODUCTO_PROV.PrecioUnitario as precioU ,PRODUCTO_PROV.Presentacion as presentacion ,PRODUCTO_PROV.Estado as estado
from PRODUCTO_PROV
PRODUCTO_PROV.Descripcion like #{descripcion}
AND PRODUCTO_PROV.Nombre like #{nombre}
AND PRODUCTO_PROV.Estado like #{estado}

mapper.update:
update PRODUCTO_PROV set 
IdProveedor=#{proveedor}
 ,Nombre=#{nombre}
 ,Descripcion=#{descripcion}
 ,PrecioUnitario=#{precioU}
 ,Estado=#{estado}
 where IdProductoProv= #{idProdProveedor}
