mapper.select:
select PROVEEDOR.IdProveedor as codigo ,PROVEEDOR.RazonSocial as razonSocial ,PROVEEDOR.Ruc as ruc ,PROVEEDOR.NombresContacto as contacto ,PROVEEDOR.Correo as correo ,PROVEEDOR.Direccion as direccion
from PROVEEDOR
PROVEEDOR.RazonSocial like #{razonSocial}
AND PROVEEDOR.Ruc=#{ruc}

mapper.update:
update PROVEEDOR set IdProveedor=#{codigo}
 ,RazonSocial=#{fechaInicio}
 ,Ruc=#{fechaFin}
 ,Direccion=#{dir}
 ,Correo=#{correo}
 ,NombresContacto=#{nombre}
 where IdPromocion= #{codigo}
