mapper.select:
select SERVICIO_PROV.IdServicioProv as codigo ,SERVICIO_PROV.Nombre as nombreServicio ,PROVEEDOR.RazonSocial as nombreProveedor ,SERVICIO_PROV.Estado as estado ,SERVICIO_PROV.Precio as precio from SERVICIO_PROV,PROVEEDOR
PROVEEDOR.IdProveedor=SERVICIO_PROV.IdProveedor
AND SERVICIO_PROV.Nombre like #{nombreServicio}
OR PROVEEDOR.RazonSocial like #{nombreProveedor}

mapper.update:
update PROMOCION set Nombre=#{nombre}
 ,FechaInicio=#{fechaInicio}
 ,FechaFin=#{fechaFin}
 ,Descripcion=#{descripcion}
 where IdPromocion= #{codigo}
