select SOLICITUD_PRODUCTO.IdSolicitudProducto as idSolicitudProducto ,SOLICITUD_PRODUCTO.idProductoProv as idProductoProv from SOLICITUD_PRODUCTO
SOLICITUD_PRODUCTO.IdProductoProv like #{idProductoProv}
select IdSede as codigo, Nombre as nombre from SEDE
insert into SOLICITUD_PRODUCTO(IdSolicitudProducto,IdProductoProv,IdSede,PrecioUnitario,Cantidad,Fecha,Estado) values (#{idSolicitudProducto},#{idProductoProv},#{idSede},#{precio},#{cantidad},#{fecha},'Nueva');
select max(IdSolicitudProducto) from SOLICITUD_PRODUCTO
select SOLICITUD_PRODUCTO.IdSolicitudProducto as idSolicitudProducto ,SOLICITUD_PRODUCTO.idProductoProv as idProductoProv ,SOLICITUD_PRODUCTO.Estado as estado ,SOLICITUD_PRODUCTO.PrecioUnitario as precio ,SOLICITUD_PRODUCTO.Cantidad as cantidad ,SOLICITUD_PRODUCTO.Fecha as fecha ,SOLICITUD_PRODUCTO.IdSede as idSede
from SOLICITUD_PRODUCTO where IdSolicitudProducto=#{idSolicitudProducto}
