mapper.select:
select SOLICITUD_INGRESO.IdSolicitudIngreso as codSolicitudSocio ,SOLICITUD_INGRESO.Nombres as nombres ,SOLICITUD_INGRESO.ApePaterno as apellidoPaterno ,SOLICITUD_INGRESO.ApeMaterno as apellidoMaterno ,SOLICITUD_INGRESO.FechaRegistro as fechaRegistro ,SOLICITUD_INGRESO.Estado as estado from SOLICITUD_INGRESO
(SOLICITUD_INGRESO.Nombres) like #{nombres}
AND (SOLICITUD_INGRESO.ApePaterno) like #{apellidoPaterno}
AND (SOLICITUD_INGRESO.ApeMaterno) like #{apellidoMaterno}

select SOLICITUD_INGRESO.IdSolicitudIngreso as codSolicitudSocio ,SOLICITUD_INGRESO.Nombres as nombres ,SOLICITUD_INGRESO.ApePaterno as apellidoPaterno ,SOLICITUD_INGRESO.ApeMaterno as apellidoMaterno ,SOLICITUD_INGRESO.FechaNac as fechaNacimiento ,SOLICITUD_INGRESO.FechaRegistro as fechaRegistro ,SOLICITUD_INGRESO.Estado as estado ,SOLICITUD_INGRESO.Direccion as direccion from SOLICITUD_INGRESO
(SOLICITUD_INGRESO.Nombres) like #{nombres}
AND (SOLICITUD_INGRESO.ApePaterno) like #{apellidoPaterno}
AND (SOLICITUD_INGRESO.ApeMaterno) like #{apellidoMaterno}
AND SOLICITUD_INGRESO.Estado='Aceptada'
