mapper.select:
SELECT inf245g2.HORARIO.IdHorario AS codigo ,inf245g2.HORARIO.Descripcion AS nombre ,inf245g2.DIA_SEMANA.IdDiaSemana AS codDiasemana ,inf245g2.HORARIOxDIA_SEMANA.HoraInicio AS horaInicio ,inf245g2.HORARIOxDIA_SEMANA.HoraFin AS horaFin
FROM inf245g2.HORARIO, inf245g2.DIA_SEMANA, inf245g2.HORARIOxDIA_SEMANA WHERE
inf245g2.HORARIO.IdHorario = inf245g2.HORARIOxDIA_SEMANA.IdHorario AND inf245g2.DIA_SEMANA.IdDiaSemana = inf245g2.HORARIOxDIA_SEMANA.IdDiaSemana
AND inf245g2.HORARIO.Descripcion like #{nombre}
ORDER BY 1 ASC ;
