mapper.select:
select MEMBRESIA.IdMembresia as codigo ,MEMBRESIA.FechaInicio as limInicio ,MEMBRESIA.FechaFin as limFin ,CONCAT(PERSONA.Nombres,' ',PERSONA.ApePaterno,' ',PERSONA.ApeMaterno) as nombre ,MEMBRESIA.Estado as estado ,MEMBRESIA.Costo as costo ,MEMBRESIA.Periodo as periodo from MEMBRESIA, PERSONA
MEMBRESIA.IdSocio=PERSONA.IdPersona and (PERSONA.Nombres LIKE #{nombre} or PERSONA.ApePaterno LIKE #{nombre} or PERSONA.ApeMaterno LIKE #{nombre})
AND MEMBRESIA.FechaInicio >= #{limInicio}
AND MEMBRESIA.FechaFin <= #{limFin}
AND MEMBRESIA.Estado= #{estado}

mapper.update:
update MEMBRESIA set FechaInicio=#{fechaInicio}
 ,FechaFin=#{fechaFin}
 ,idSocio=#{codigoSocio}
 ,Costo=#{costo}
 ,Periodo=#{periodo}
 ,Estado=#{estado}
 where IdMembresia= #{idMembresia}
