mapper.select:
select PRODUCTO_PROV.IdProdProveedor as codigo ,PRODUCTO_PROV.IdProveedor as proveedor ,PRODUCTO_PROV.Nombre as nombre ,PRODUCTO_PROV.Descripcion as desc ,PRODUCTO_PROV.PrecioUnitario as pu ,PRODUCTO_PROV.Presentacion as pre ,PRODUCTO_PROV.Estado as estado
from PRODUCTO_PROV
PRODUCTO_PROV.IdProdProveedor= #{codigo}
PRODUCTO_PROV.IdProveedor= #{proveedor}
AND PRODUCTO_PROV.Nombre= #{nombre}
AND PRODUCTO_PROV.Estado='Activo'

mapper.update:
update PRODUCTO_PROV set 
IdProveedor=#{proveedor}
 ,Nombre=#{nombre}
 ,Descripcion=#{desc}
 ,PrecioUnitario=#{pu}
 ,Estado=#{estado}
 where IdProdProveedor= #{codigo}
